con:configuration.scriptText:
import com.eviware.soapui.support.XmlHolder
def holder = new XmlHolder(messageExchange.responseContentAsXml)
def CustomerID= holder.getNodeValue("//customerId")
context.testCase.setPropertyValue("customerId",CustomerID)

con:config.script:
//how to use a different test case inside a test case. here test case 1 is used inside test case 2
import com.eviware.soapui.model.testsuite.TestRunner.Status
def tc = testRunner.testCase.testSuite.project.testSuites["TestSuite 1"].testCases["PropertyExpansion"]

//to set or update the properties value
tc.testSteps["Properties"].setPropertyValue("email","madbanke123@ibm.com")
tc.testSteps["Properties"].setPropertyValue("firstname","Krishna")
tc.testSteps["Properties"].setPropertyValue("lastname","Bankey")

def runner = tc.run(null,false)
log.info "Status=$runner.status, time taken to complete test case is:$runner.timeTaken ms"
log.info "number of test steps executed are :"+runner.results.size()
